package dsa.twopointers;

import java.util.Map;

import dsa.ISolution;

/**
 * 392. IS SUBSEQUENCE
 * 
 * Given two strings s and t, return true if s is a subsequence of t, or false
 * otherwise.
 * 
 * A subsequence of a string is a new string that is formed from the original
 * string by deleting some (can be none) of the characters without disturbing
 * the relative positions of the remaining characters. (i.e., "ace" is a
 * subsequence of "abcde" while "aec" is not).
 * 
 * 
 * 
 * Example 1:
 * 
 * Input: s = "abc", t = "ahbgdc"
 * Output: true
 * 
 * Example 2:
 * 
 * Input: s = "axc", t = "ahbgdc"
 * Output: false
 * 
 * 
 * 
 * Constraints:
 * 
 * 0 <= s.length <= 100
 * 0 <= t.length <= 104
 * s and t consist only of lowercase English letters.
 * 
 * 
 * Follow up: Suppose there are lots of incoming s, say s1, s2, ..., sk where k
 * >= 109, and you want to check one by one to see if t has its subsequence. In
 * this scenario, how would you change your code?
 */

public class IsSubsequence implements ISolution {

    public void execute(Object arg) {

        Map<String, String> trailMap = (Map<String, String>) arg;
        for (String key : trailMap.keySet()) {
            System.out.println("Main string : " + trailMap.get(key));
            System.out.println("Test string : " + key);
            System.out.println("isSubseq : " + isSubsequence2(key, trailMap.get(key)));
        }
    }

    public boolean isSubsequence2(String s, String t) {

        String mainS = t;
        int subseqIndexAtMainString = 0;
        int prev = 0;
        if(t.contains(s)){
            return true;
        }
        for (int i = 0; i < s.length(); i++) {
            subseqIndexAtMainString = mainS.indexOf("" + s.charAt(i),prev);
            if (subseqIndexAtMainString == -1) {
                return false;
            }
            if (subseqIndexAtMainString < prev) {
                return false;
            }
          
            mainS = mainS.replaceFirst("" + s.charAt(i), " ");
            prev = subseqIndexAtMainString;
        }
        return true;

    }

    public boolean isSubsequence(String s, String t) {

        String mainS = t;
        char[] subseq = s.toCharArray();
        for (char x : subseq) {
            if (mainS.contains("" + x)) {
                mainS.replaceFirst("" + x, " ");
            } else {
                return false;
            }
        }
        return true;

    }

    public static String longString = "";
}